import React from "react";
import "../App.css";
import ArrowDown from "../images/icon-arrow-down.svg";


class QuesAnsw extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            actived: false
        };
    }

     handleClick() {
         this.setState(prevState =>({
             actived: !prevState.actived
         }));
             if(this.state.actived)
                document.querySelector(".answer").style.display = "block";
            else
            document.querySelector(".answer").style.display = "none";


    }
    render() {
        return (
            <div className="row_ques">

            <div onClick={this.handleClick} className="question">
            <h4>{this.props.question}</h4>
                <img className="arrow-img" src={ArrowDown} />
            </div>
            <div className="answer">
                <p>{this.props.answer}</p>
            </div>

            </div>

        );
    }
}

export default QuesAnsw;
